import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { Subject } from 'rxjs';
import { BrowserRouter as Router, Route, Link , Switch} from "react-router-dom";

import {createStore, combineReducers, thunk} from 'react-redux';
const subject = new Subject();

class FirstComp extends Component{

	constructor(props){
		super(props)

		this.state={
			data: "TEXT"
		}


	}
	componentDidMount(){
		let _this = this;
		this.props.subject.subscribe((props)=>{ 
			_this.setState({
				data: props.value
			})
		});



	}


	componentWillUnmount(){


	}
	render(){
		return(
		<div className="ob">
			<h1>{this.state.data}</h1>
		</div>
		)

	}

}



class SecondComp extends Component{

	constructor(props){
		super(props)

		this.state={
			value: "TEXT"
		}

		this.onChangeHandler = this.onChangeHandler.bind(this);
	}
	componentDidMount(){
		let _this = this;


	}


	componentWillUnmount(){


	}

	onChangeHandler(e){ 
		let _this = this;
		let val = e.target.value;
		let props = {value: val};
		this.props.subject.next(props);


		this.setState({
			value:e.target.value
			});

	}


	render(){
		return(
		<div className="ob">
			<input onChange={this.onChangeHandler} value={this.state.value}  />
		</div>
		)

	}

}


class ThirdComp extends Component{

	constructor(props){
		super(props)

		this.state={
			data: []
		}


	}
	componentDidMount(){
		let _this = this;
		this.props.subject.subscribe((props)=>{ 
			let data = props.value;
			let _data = this.state.data.slice(0);
			_data.push(data);

			_this.setState({
				data: _data
			})
		});
	}


	componentWillUnmount(){


	}



	render(){
		return(
		<div className="ob">
			<Link to={"/textboxes"}>TEXTBOXES</Link>
			<br/>
			<Link to={"/login"}>Login</Link>

		</div>
		)

	}

}

class Main extends Component{
	render(){
		return(

				<div className="row">
					<div className="col">
						<div className="block_rxjs_subject">
							<ThirdComp subject={subject}/>

						</div>
					</div>
			</div>

		)
	}
}


class TextBoxes extends Component{
	constructor(props){
		super(props)
	}

	render(){
		return(
				<div className="row">
				<div className="col">
					<h1>RXJS компоненты</h1> 
						<div className="block_rxjs_subject">
						<Link to={"/"}>Назад</Link>
						<br/>
							<FirstComp subject={subject}/>
							<SecondComp  subject={subject}/>
						</div>
					</div>  
			</div>

		)
	}
}

class NotFound extends Component{
	constructor(props){
		super(props)
	}

	render(){
		return(
				<div className="row ">
				<div className="col">
					<h1 className=" block_rxjs_subject">Страница не найдена</h1> 
				</div>
			</div>

		)
	}
}

class Logger extends Component{
	constructor(props){
		super(props)

		this.state={
			userName: "",
			userPass: "",
		}




	}

	componentDidMount(){
			let accessToken = null;

	}

	changeValue(e){
		let value = e.target.value;
		let field = e.target.name;

		let state = Object.assign({}, this.state);
		state[field] = value;
		this.setState(state)

	}

	render(){
		return(
				<div className="row">
					<div className="col">
					<h1>Регистрация</h1>
					<div className=" block_rxjs_subject">
						<Link to={"/"}>Назад</Link>
						<br/>
					<p>Зарегистрируйтесь</p>
					<p><input type="text" value={this.state.userName} onChange={this.changeValue.bind(this)} name="userName" /></p>
					<p><input type="text" value={this.state.userPass} onChange={this.changeValue.bind(this)} name="userPass" /></p>
					<p>
						<button>Войти</button>
						<Link to={"/registration"}>Зарегистрироваться</Link> 
					</p>
					<br/>
					<p>Забыли пароль? </p>
					<p>

						<Link to={"/resetpass"}>Восстановить пароль</Link>
					</p>
					</div>
					</div>
				</div>

		)
	}
}




class App extends Component {
  render() {
    return (
      	<div className="App">
        <header className="App-header"> 
          <h1 >
