import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App'; 
import {Provider, connect} from  'react-redux';
import {createStore, applyMiddleware} from 'redux';
import createSagaMiddleware from  'redux-saga';
import {takeEvery}  from  'redux-saga/effects';
import {put, call}  from  'redux-saga/effects';

// Reducer
const initialState = {
  url: '',
  loading: false,
  error: false,
  user:null
};
const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'LOGIN_START':
      return {
        url: '',
        loading: true,
        error: false,
      };
    case 'LOGIN_SUCCEDED':
      return {
        url: action.url,
        loading: false,
        error: false,
      };
    case 'LOGIN_FAILED':
      return {
        url: '',
        loading: false,
        error: true,
      };
	case 'REGISTRATION_START':
      return {
        url: '',
        loading: true,
        error: false,
      };
    case 'REGISTRATION_SUCCEDED':
      return {
        url: action.url,
        loading: false,
        error: false,
      };
    case 'REGISTRATION_FAILED':
      return {
        url: '',
        loading: false,
        error: true,
      };
	case 'RESTORE_LINK_START':
      return {
        url: '',
        loading: true,
        error: false,
      };
    case 'RESTORE_LINK_SUCCEDED':
      return {
        url: action.url,
        loading: false,
        error: false,
      };
    case 'RESTORE_LINK_FAILED':
      return {
        url: '',
        loading: false,
        error: true,
      };
    default:
      return state;
  }
};

// Action Creators
const registerUserStart = () => {
  return { type: 'REGISTRATION_START' }
};
const registerUserSuccess = () => {
  return { type: 'REGISTRATION_SUCCEDED' }
};

const registerUserFailed = () => {
  return { type: 'REGISTRATION_FAILED' }
};


const restoreUserMailStart = () => {
  return { type: 'RESTORE_LINK_START' }
};

const restoreUserMailSuccess = () => {
  return { type: 'RESTORE_LINK_SUCCEDED' }
};

const restoreUserMailFail = () => {
  return { type: 'RESTORE_LINK_FAILED' }
};




const registrationSuccess = (data) => {
  return { type: 'REGISTRATION_SUCCEDED', url: data.message }
};

const requestDogError = () => {
  return { type: 'REQUESTED_DOG_FAILED' }
};



// Sagas
function* userSaga() { 
  yield takeEvery('RESTORE_LINK', restoreUserMail);
  yield takeEvery('SIGNIN', signInUser);
  yield takeEvery('REGISTRATION', registerUser);
}

function* registerUser() {
  try {
    yield put(registerUserStart());
    const data = yield call(() => {
      return fetch('/registeruser')
              .then(res => res.json())
      }
    );
    yield put(registerUserSuccess(data));
  } catch (error) {
    yield put(registerUserFailed());
  }
}

function* restoreUserMail() {
  try {
    yield put(restoreUserMailStart());
    const data = yield call(() => {
      return fetch('/registration')
              .then(res => res.json())
      }
    );
    yield put(requestDogSuccess(data));
  } catch (error) {
    yield put(requestDogError());
  }
}

function* signInUser() {
  try {
    yield put(requestDog());
    const data = yield call(() => {
      return fetch('https://dog.ceo/api/breeds/image/random')
              .then(res => res.json())
      }
    );
    yield put(requestDogSuccess(data));
  } catch (error) {
    yield put(requestDogError());
  }
}

// Component


// Store
const sagaMiddleware = createSagaMiddleware();
const store = createStore(
  reducer,
  applyMiddleware(sagaMiddleware)
);
sagaMiddleware.run(userSaga);

const ConnectedApp = connect((state) => {
  console.log(state);
  return state;
})(App);

// Container component
ReactDOM.render(
  <Provider store={store}>
    <ConnectedApp />
  </Provider>,
  document.getElementById('root')
); 

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
